#设置编码格式
SET NAMES UTF8;
#丢弃数据库
DROP DATABASE IF EXISTS idong;
CREATE DATABASE idong CHARSET=UTF8;
USE idong;

/*******************/
/******创建表******/
/*******************/
/**表01、用户信息表   idong_user**/
CREATE TABLE idong_user(
  user_id INT PRIMARY KEY AUTO_INCREMENT COMMENT '用户id，主键且自增',
  user_name VARCHAR(16) COMMENT '用户昵称，唯一约束',
  user_password VARCHAR(32) COMMENT '用户密码,(md5加密)',
  user_phone VARCHAR(11) COMMENT '用户手机号(用户登录注册)',
  user_avatar VARCHAR(128) DEFAULT 'unnamed.jpg' COMMENT '用户头像路径',
  user_sex INT DEFAULT '0' COMMENT '性别  0-女  1-男',
  user_birth VARCHAR(32) COMMENT '用户生日',
  user_desc TEXT COMMENT '用户个性签名'
)ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8;


/******01_用户信息表数据插入******/


/**表02、用户关注表   user_follow**/
CREATE TABLE user_follow(
  follow_id INT PRIMARY KEY AUTO_INCREMENT COMMENT '关注id，主键且自增',
  user_id INT COMMENT '用户id，外键，参照用户信息表用户id',
  followed_user_id INT COMMENT '被关注用户id，外键，参照用户信息表用户id'
)ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8;

/******02_用户关注表数据插入******/

/**表03、打卡签到表   user_sign**/
CREATE TABLE user_sign(
  sign_id INT PRIMARY KEY AUTO_INCREMENT COMMENT '签到id，主键且自增',
  user_id INT COMMENT '用户id，外键，参照用户信息表用户id',
  sign_at INT COMMENT '签到时间，存放距计算机元年毫秒数'
)ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8;


/******03_打卡签到表数据插入******/



/**表04、用户动态表   user_post**/
CREATE TABLE user_post(
  post_id INT PRIMARY KEY AUTO_INCREMENT COMMENT '动态id，主键且自增',
  user_id INT COMMENT '用户id，外键，参照用户信息表用户id',
  post_content MEDIUMTEXT COMMENT '动态正文(动态详细信息)',
  post_at INT COMMENT '动态发布时间,存放距计算机元年毫秒数',
  post_img_num INT COMMENT '用户本次所发布动态所上传的图片数量'
)ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8;

/**表05、动态图片表   post_img**/
CREATE TABLE user_img(
  img_id INT PRIMARY KEY AUTO_INCREMENT COMMENT '动态图片id，主键且自增',
  post_id INT COMMENT '动态表id，外键，参照用户动态表id',
  img_name VARCHAR(128) COMMENT '图片存放路径',
  img_oid INT COMMENT '图片插入顺序，例如用户上传2张图就分别给图片加上1,2的编号，放在这里，显示时按顺序显示'
)ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8;

/**表06、动态评论表   post_review**/
CREATE TABLE post_review(
  review_id INT PRIMARY KEY AUTO_INCREMENT COMMENT '动态评论表id，主键且自增',
  user_id INT COMMENT '评论用户id，外键，参照用户信息表用户id',
  review_content TEXT COMMENT '用户所评论内容',
  review_at INT COMMENT '评论创建时间',
  review_upid INT COMMENT '评论上一级id（例如若回复动态，为动态id，若   回复评论，为评论id',
  review_type INT COMMENT '评论类型（1-回复动态，2回复评论)'
)ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8;

/**表07、动态点赞表   post_like**/
CREATE TABLE post_like(
  like_id INT PRIMARY KEY AUTO_INCREMENT COMMENT '动态点赞表id，主键且自增',
  post_id INT COMMENT '所评论动态表id，外键，参照用户动态表id',
  user_id INT COMMENT '评论用户id，外键，参照用户信息表用户id'
)ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8;


/**表08、百科类别表   ency_category**/
DROP TABLE IF EXISTS `ency_category`;
CREATE TABLE `ency_category` (
  `ency_category_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '百科类别表id，主键且自增',
  `ency_category_name` varchar(16) DEFAULT NULL COMMENT '类别名称',
  PRIMARY KEY (`ency_category_id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of ency_category
-- ----------------------------


/**表09、百科文章表   ency_article**/
DROP TABLE IF EXISTS `ency_article`;
CREATE TABLE `ency_article` (
  `article_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '文章id，主键且自增',
  `ency_category_id` int(11) DEFAULT NULL COMMENT '文章类别id，外键，参照百科类别id',
  `article_title` varchar(64) DEFAULT NULL COMMENT '文章标题',
  `is_hot` int(11) DEFAULT NULL COMMENT '是否热度(默认0不是热度，1是热度),可根据用户文章收藏数量和创建时间判定',
  `article_at` int(50) DEFAULT NULL COMMENT '文章创建时间',
  `article_image` varchar(50) DEFAULT NULL COMMENT '文章缩略图',
  `article_content` mediumtext COMMENT '文章正文',
  `read_count` int(11) DEFAULT '0' COMMENT '阅读次数，点击+1',
  PRIMARY KEY (`article_id`)
) ENGINE=InnoDB AUTO_INCREMENT=39 DEFAULT CHARSET=utf8;


-- ----------------------------
-- Records of ency_article
-- ----------------------------

/**表10、用户文章收藏表   article_collect**/
CREATE TABLE article_collect(
  collect_id INT PRIMARY KEY AUTO_INCREMENT COMMENT '收藏id，主键且自增',
  user_id INT COMMENT '用户id,外键,参照用户信息id',
  collect_upid INT COMMENT  '收藏类型id（例如若收藏动态，为动态id，若   收藏文章，为文章id）',
  collect_type INT COMMENT '收藏类型（1-收藏动态，2收藏文章）'
)ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8;

/**表11、运动类别表   sport_category**/
CREATE TABLE sport_category(
  sport_category_id INT PRIMARY KEY AUTO_INCREMENT COMMENT '运动类别表id，主键且自增',
  sport_categoryr_name VARCHAR(32) COMMENT '运动类别名称'
)ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8;


/**表12、运动课程表   sport_class**/
CREATE TABLE sport_class(
  class_id INT PRIMARY KEY AUTO_INCREMENT COMMENT '运动课程表id，主键且自增',
  class_name VARCHAR(32) COMMENT '课程名称',
  class_desc VARCHAR(128) COMMENT '课程简介',
  class_img VARCHAR(32) COMMENT '课程图片存放路径',
  sport_category_id INT COMMENT '课程类别id，外键,参照用户信息id'
)ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8;

/**表13、用户课程记录表   sport_user**/
CREATE TABLE sport_user(
  su_id INT PRIMARY KEY AUTO_INCREMENT COMMENT '用户课程记录表id，主键且自增(用户每观看一条视频生成一条用户课程记录)',
  user_id INT COMMENT '用户id,外键,参照用户信息id',
  class_id INT COMMENT '课程id，外键,参照运动课程表id',
  video_id INT COMMENT '所记录该视频id，外键,参照课程视频表id',
  video_at_id INT COMMENT '记录观看该视频的时间'
)ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8;

/**表14、课程视频表   class_videor**/
CREATE TABLE class_video(
  video_id INT PRIMARY KEY AUTO_INCREMENT COMMENT '课程视频表id，主键且自增(记录所有课程中的所有视频信息)',
  video_name VARCHAR(32) COMMENT '视频存放路径',
  class_id INT COMMENT '课程id，外键,参照运动课程表id',
  video_oid INT COMMENT '该视频是所在课程的第n个视频'
)ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8;


